ALTER TABLE `crf`.`deposit_account_payments` 
ADD COLUMN `date_period_from` DATETIME NULL AFTER `date_processed`,
ADD COLUMN `date_period_to` DATETIME NULL AFTER `date_period_from`;

CREATE TABLE crf.`deposit_account_documents` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `code` varchar(64) NOT NULL,
  `type` int(11) NOT NULL,
  `customer_id` int(11) NOT NULL,
  `deposit_account_id` int(11) NOT NULL,
  `deposit_product_id` int(11) NOT NULL,
  `deposit_account_payment_id` int(11) NOT NULL,
  `account_number` varchar(8) NOT NULL,
  `path` varchar(128) NOT NULL,
  `date_created` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `customer_id_ik` (`customer_id`),
  KEY `deposit_account_id_ik` (`deposit_account_id`),
  KEY `deposit_product_id_ik` (`deposit_product_id`),
  KEY `deposit_account_payment_id_ik` (`deposit_account_payment_id`),
  KEY `date_created_ik` (`date_created`),
  KEY `code_ik` (`code`)
) ENGINE=InnoDB AUTO_INCREMENT=1001 DEFAULT CHARSET=utf8;
